INSERT INTO `hos_board` VALUES ('2', 'เมื่อความสำเร็จในเครื่องมือเดิมๆ มันไม่สามารถตอบโจทย์เราได้  เราก็แค่เปลี่ยนเครื่องมือ  หรือหาเครื่องมือเพิ่ม..', '<p>&nbsp;&nbsp;</p>\r\n<p><span style=\"color: rgb(128, 0, 0);\"><span style=\"font-size: medium;\">สวัสดีครับ &nbsp;นักพัฒนาทุกท่าน</span></span></p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\"> &nbsp; &nbsp; &nbsp;ผมก็เป็นอีกคนหนึ่งนะครับที่ต้องการความสำเร็จ &nbsp;แล้วความสำเร็จนั้นคืออะไร &nbsp; นั่นแหระคือสิ่งที่เราต้องคิด &nbsp;&nbsp;</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">&nbsp;การตั้งเป้าหมายในชีวิตว่าต้องการทำอะไรในชีวิต &nbsp;เพราะคนเราเกิดมาไม่นานเดียวก็ต้องจากโลกนี้ไปแล้ว &nbsp;เอาเวลาที่เรามีอยู่มาสรรค์สร้างผลงาน</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">เพื่อเป็นตำนานฝากไว้ในโลกนี้ดีกว่า ว่าไหมครับ &nbsp;</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ก่อนที่ผมจะจับต้นชนปลายเพื่อเข้ามาสู่วงการนักพัฒนาโปรแกรมได้ &nbsp;ผมต้องผ่านอะไรมาบ้าง &nbsp;..ผ่านเยอะครับ เพราะสมัยผมนั้นไม่มี &nbsp;Facebook &nbsp;Youtube &nbsp;</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">หรือแม้แต่สื่อต่างๆ &nbsp;( ผมไม่ได้เก่งอะไรนะครับ &nbsp;แค่ทำได้แก้ปัญหาได้เพียงเท่านั้นเอง &nbsp;) ความพยยาม &nbsp;ความอดทน &nbsp;และหมั่นศึกษาจะพาเราไป &nbsp;.. ผมเคยน้ำตาไหลมานับไม่ถ้วน &nbsp;</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">ท่านก็เช่นกัน &nbsp;ต้องก้าวข้าวด่านอรหันต์ของวิชาที่ว่าด้วย นักพัฒนาโปรแกรมให้ได้นะครับ แล้วท่านก็จะสนุก &nbsp;และหลงไหนในการทำงาน &nbsp;</span></span></p>\r\n<p><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: medium;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: rgb(255, 0, 255);\">&quot; วันนี้แค่นี้ก่อนครับร้อนมาเลย &nbsp;ให้กำลังใจกัน &nbsp; และของคุณอาจารย์ทุกสำนัก &nbsp;ทุกแห่งเรียนรู้ &nbsp;เพื่อนร่วมอุดมณ์การทุกท่าน &nbsp; สวัสดีครับ</span></span></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/2/1926136_10152258944786462_1488030702_o.jpg\" width=\"750\" height=\"498\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', 'huwko', '2014-03-28 13:58:14', '2016-05-29 08:59:39', '290', null, '', '0000-00-00 00:00:00', '118.175.94.103');
INSERT INTO `hos_board` VALUES ('3', 'การสร้าง Class PHP เพื่อใช้ในการเชื่อมต่อฐานข้อมูล Mysql', '<p><span style=\"color: rgb(0, 0, 255);\">&nbsp;&lt;?php</span></p>\r\n<div><span style=\"color: rgb(0, 0, 255);\">class connectdb</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\">{</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>public $conn;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>public $ServerName=&quot;localhost&quot;;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>public $UserName=&quot;UserName&quot;;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>public $UserPassword= &quot;Password&quot;;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>public $DatabaseName=&quot;db_name&quot;;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span>function Conn2DB()&nbsp;</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">			</span>{</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">				</span>$this-&gt;conn= mysql_connect($this-&gt;ServerName,$this-&gt;UserName,$this-&gt;UserPassword);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">				</span>if(! $this-&gt;conn )</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">					</span>die(&quot;ไม่สามารถติดต่อกับ MYSQL ได้ กรุณาตรวจสอบ&quot;);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">				</span>mysql_select_db( $this-&gt;DatabaseName,$this-&gt;conn)</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">					</span>or die(&quot;ไม่สามารถเลือกใช้ฐานข้อมูล MYSQL ได้&quot;);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">					</span>mysql_query(&quot;SET character_set_results=utf8&quot;);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">					</span>mysql_query(&quot;SET character_set_client=utf8&quot;);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">					</span>mysql_query(&quot;SET character_set_connection=utf8&quot;);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">			</span>}</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span> function Close2DB() //propertie cloce DB</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">			</span>{</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">				</span>mysql_close($this-&gt;conn);</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">			</span>}<span class=\"Apple-tab-span\" style=\"white-space: pre;\">		</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\">	</span>}</span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\">?&gt;</span></div>', 'wikarn', '2014-03-28 20:23:46', '2014-03-28 21:05:02', '294', null, '', '0000-00-00 00:00:00', '115.87.79.203');
INSERT INTO `hos_board` VALUES ('4', 'จำครั้งแรกที่ผมเปลี่ยนความคิดวิธีคิด 13 Jun 13  จากคนทั่วไป 95%  พยายามก้าวข้ามมาอยู่ใน 5 เปอร์เซนต์ให้ได้ เปิดตัวตำราจากการตกผลึกที่โรงแรม ในสุรินทร์..', '<p style=\"text-align: center;\">&nbsp;ขอบคุณพี่ไก่ &nbsp;พี่พิท &nbsp;พี่ลพ &nbsp;และทุกคนที่ให้การสนับสนุน &nbsp;วางโชว์ &nbsp;50 &nbsp;เล่มเพื่อร่วมกิจกรรม แต่ไม่น่าเชื่อว่ามีคนสนใจเยอะมากครับ.. หมดเกลี้ยง</p>\r\n<p style=\"text-align: center;\">เปลี่ยนวิธีคิดชีวิต เปลี่ยนจิงๆครับ &nbsp; &nbsp;หลังจากนั้นผมก็เริ่มคิดว่าเกิดมาทำไม &nbsp;และเริ่มอ่านหนังสือติดงอมแงม &nbsp;</p>\r\n<p style=\"text-align: center;\">หนังสือที่อ่านก็เป็นแนวหนังสือคนประสบความสำเร็จ &nbsp;วิธีการที่จะไปถึงจุดนั้น &nbsp;และหนังสือขายดี &nbsp;อ่านแทบหมดทั้งร้าน . แบบว่าว่างเป็นไม่ได้..</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/2/621537_10151639899281462_1078856066_o.jpg\" width=\"604\" height=\"400\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/2/1015530_10151639900116462_198856890_o.jpg\" width=\"604\" height=\"400\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/2/6509_10151641122111462_1983966325_n.jpg\" width=\"600\" height=\"398\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', 'huwko', '2014-03-29 13:02:08', '2014-05-17 13:28:41', '339', null, '', '0000-00-00 00:00:00', '118.175.94.239');
INSERT INTO `hos_board` VALUES ('5', 'โปรแกรมเมอร์คนแรกของโลก', '<p><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: x-large;\">&nbsp;วันนี้เอาสาระน่ารู้มาฝาก &nbsp;ให้อ่านเป็นความรู้ในสาย &quot;โปรแกรมเมอร์ครับ &quot;</span></span></p>\r\n<h3 style=\"font-family: arial, verdana, Helvetica, sans-serif; line-height: 17.15625px; color: blue;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: 18px;\">Lady Augusta&nbsp;</span></span><span style=\"font-size: 18px;\">Ada Byron</span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: 18px;\">, Coutress of</span></span><span style=\"font-size: 18px;\">&nbsp;Lovelace</span>&nbsp;<span lang=\"EN\" style=\"font-family: Helvetica, sans-serif; font-size: 11pt;\">T</span><span lang=\"EN\" style=\"font-family: Helvetica, sans-serif; font-size: 11pt;\">he world\'s first programmer</span><br />\r\n<span style=\"color: rgb(0, 100, 0);\"><span style=\"font-size: 18px;\">เลดี้ ออคุสตา</span></span><span style=\"color: rgb(0, 128, 0);\"><span style=\"font-size: 18px;\">&nbsp;เอดา เลฟเลค&nbsp;</span></span><span style=\"color: rgb(255, 140, 0);\"><span style=\"font-size: 18px;\"><span style=\"font-size: 14px;\">ได้รับการยกย่องว่าเป็นโปรแกรมเมอร์คนแรกของโลก</span></span></span></h3>\r\n<p style=\"margin-top: 0px; margin-bottom: 5px; color: rgb(51, 51, 51); font-family: arial, verdana, Helvetica, sans-serif; font-size: 13px; line-height: 17.15625px;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เกิดเมื่อปี ค.ศ.1816 ต่อมาพ่อแม่ของเธอก็แยกทางกัน ทางคุณแม่ของเอดาคิดว่า การศึกษาจะทำให้ลูกสาวได้ประโยชน์มากในอนาคต ตอนนั้นเอดาก็เลยถูกเลี้ยงมาให้เป็นผู้หญิงสมัยใหม่ ที่เรียนรู้ทั้งเรื่องวิทยาศาสตร์และคณิตศาสตร์ ต่างจากผู้หญิงสมัยก่อนๆ คือสังคมชั้นสูง แต่งสวยไปวันๆ<br />\r\n<br />\r\n<img alt=\"Charles Babbage\" src=\"http://sorncomputer.com/picture/learning/history/computer-babbage.jpg\" style=\"border: 0px; width: 124px; height: 151px; float: right;\" />&nbsp;&nbsp;&nbsp;&nbsp; พออายุ 18 ปี เอดาได้ไปงานเลี้ยงอาหารค่ำที่บ้านของ Mrs. Somerville จนได้พบกับ</span></span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: 14px;\">Charles Babbage</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">&nbsp;</span></span><span style=\"color: rgb(178, 34, 34);\"><span style=\"font-size: 14px;\">(บิดาของคอมพิวเตอร์</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">)&nbsp;</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">ผู้เชี่ยวชาญทางด้านคณิตศาสตร์ที่ได้รับการยอมรับอย่างสูง เอดาได้สนใจในแนวคิดของ Babbage&nbsp;</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">มากๆจนอาสา เป็นผู้ช่วยงานและพัฒนา โดยสิ่งที่เธอทำคือการคิดสร้างภาษา(Progrmming)สำหรับเครื่อง Analytical Engine จากนั้นก็พัฒนาและคิดค้นแนวคิดต่างๆมาเรื่อยๆ<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; ในยุคนั้น ผู้หญิงอย่างเอดาก็ไม่ได้รับการยกย่องในงานทางวิทยาศาสตร์มากนัก แต่เธอก็ได้กำลังจากจากสามี ที่คอยให้ความเห็นอกเห็นใจ เอาใจช่วยในการฝ่าฟันปัญหาและอุปสรรคต่างๆ จนเธอได้การยอมรับในภายหลัง<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; เอดาเสียชีวิตด้วยโรคมะเร็งเมื่อปี ค.ศ.1852 ต่อมาปี ค.ศ.1979 ได้มีการตั้งชื่อคอมพิวเตอร์ที่เป็นระบบไร้คนควบคุมเครื่องหนึ่งซึ่งพัฒนาโดยกระทรวงกลาโหมของประเทศสหรัฐอเมริกาว่า &quot;</span></span><span style=\"color: rgb(0, 128, 0);\"><span style=\"font-size: 18px;\">เอดา</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">&quot; เพื่อเป็นเกียรติให้กับเธอด้วย<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size: 18px;\"><span style=\"color: rgb(0, 128, 0);\">เอดา</span></span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: 14px;\">จึงเป็นคนแรกที่มีแนวคิดว่าคอมพิวเตอร์เป็นเครื่องมือในการคำนวณ</span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 14px;\">&nbsp;และไม่เพียงแต่คำนวณได้เท่านั้น เอดายังสามารถคาดการณ์เอาไว้ล่วงหน้าได้อีกว่า ในอนาคตคอมพิวเตอร์จะสามารถนำมาใช้สร้างเสียงดนตรีและช่วยกระบวนการ อุตสาหกรรมได้</span></span></p>', 'wikarn', '2014-03-30 06:42:24', '2014-03-30 06:42:24', '521', null, '', '0000-00-00 00:00:00', '171.96.72.65');
