INSERT INTO `hos_board` VALUES ('7', 'MySQL Connector/Net connection strings', '<p><span style=\"font-size: large;\"><strong><span style=\"color: rgb(0, 0, 255);\">&nbsp;Standard</span></strong></span></p>\r\n<p><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">Server=myServerAddress;Database=myDataBase;Uid=myUsername;Pwd=myPassword;</span></span></p>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br type=\"_moz\" />\r\n</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br />\r\n</span></span></div>\r\n<div><strong><span style=\"font-size: large;\"><span style=\"color: rgb(0, 0, 255);\">Specifying TCP port</span></span></strong></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br type=\"_moz\" />\r\n</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">Server=myServerAddress;Port=1234;Database=myDataBase;Uid=myUsername;</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">Pwd=myPassword;</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br type=\"_moz\" />\r\n</span></span></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br type=\"_moz\" />\r\n</span></span></div>\r\n<div><strong><span style=\"font-size: large;\"><span style=\"color: rgb(0, 0, 255);\">Multiple servers</span></span></strong></div>\r\n<div>&nbsp;</div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\"><br type=\"_moz\" />\r\n</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">Server=serverAddress1, serverAddress2, serverAddress3;Database=myDataBase;</span></span></div>\r\n<div><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">Uid=myUsername;Pwd=myPassword;</span></span></div>\r\n<div>&nbsp;</div>', 'wikarn', '2014-04-01 12:39:24', '2014-04-01 12:39:24', '328', null, '', '0000-00-00 00:00:00', '124.120.219.209');
