INSERT INTO `hos_news` VALUES ('4', null, '28 มิถุนายน 56  รณรงค์รวมพลคนรักมดลูก ดีใจจังสตรีชาวชุมพลบุรีใส่ใจสุขภาพตนเองมากขึ้น ของรางวัลเป็นเพียงแรงจูงใจ แต่สิ่งที่ยิ่งใหญ่คือการได้ดูแลสุขภา', '<p>เรียนรู้การพัฒนาโปรแกรมกับ VDO  สำหรับใครที่ไม่ชอบการอ่านเท่าไหร่นัก อย่าพลาด ...</p>', '2013-07-31 07:58:02', 'admin', '777', '/userfiles/image/Image%20mikato/1A%2056/1i%20%E0%B8%84%E0%B8%99%E0%B8%A3%E0%B8%B1%E0%B8%81%E0%B8%A1%E0%B8%94%E0%B8%A5%E0%B8%B9%E0%B8%81/File1525.JPG', '', null, null, 'True', '182.52.28.82', 'A', '1', null);
INSERT INTO `hos_news` VALUES ('27', null, 'พบเด็กเป็นมือเท้าปาก มาตรวจที่โรงพยาบาลช่วงสัปดาห์ที่่่่ผ่านมา(๒๘ กรกฎาคม )', '<p>&nbsp;เย็นวันที่ ๒๘ กรกฎาคม ๒๕๕๖ โรงพยาบาลชุมพลบุรีได้รับแจ้งจากแพทย์ประจำ OPD ว่ามีเด็กป่วยด้วยอาการ เป็นผื่นตุ่มน้ำที่มือ เท้า ปาก บางรายเป็นที่เท้าร่วมด้วย การวินิจฉัย สงสัยป่วยด้วย enterovirus เป็นมือ เท้าปาก ขณะนี้ได้ดดำเนินการสอบสวนโดยเจ้าหน้าที่และนักศึกษาสถาบันราชภัฏในพื้นที่แล้ว ความคืบหน้าโรงพยาบาลจะแจ้งให้ทราบอีกครั้งนะครับ (งานระบาดวิทยา)</p>', '2013-07-31 09:45:34', 'admin', '204', '/userfiles/image/Image%20mikato/1A/%E0%B8%A1%E0%B8%B7%E0%B8%AD%E0%B9%80%E0%B8%97%E0%B9%89%E0%B8%B2%E0%B8%9B%E0%B8%B2%E0%B8%81.jpg', null, null, null, 'True', '182.52.28.82', 'A', '1', null);
