INSERT INTO `hos_news` VALUES ('26', null, 'ฉีดวัคซีนป้องกันไข้หวัดใหญ่ 2013 ให้กับผู้สูงอายุ 65 ปีขึ้นไป', '<p>โรงพยาบาลชุมพลบุรี ได้มีการฉีดวัคซีนป้องกันไข้หวัดใหญ่ 2013 ให้กั<strong>บผู้สูงอายุ 65 ปีขึ้นไป</strong> ที่อยู่ในเขตอำเภอชุมพลบุรี <strong>ในวันศุกร์ที่ 26 กรกฎาคม พ.ศ. 2556</strong> และ <strong>วันศุกร์ที่ 2 สิงหาคม พ.ศ. 2556&nbsp;</strong>ติดต่อขอ รับวัคซีนได้ที่ฝ่ายส่งเสริมสุขภาพ โรงพยาบาลชุมพลบุรี ตามวันที่ดังกล่าว <strong>เวลา 08:00 - 12:00 น.</strong></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A/DSC_0769.JPG\" width=\"800\" height=\"530\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A/DSC_0781.JPG\" width=\"800\" height=\"530\" alt=\"\" /><img src=\"/userfiles/image/Image%20mikato/1A/DSC_0783.JPG\" width=\"800\" height=\"530\" alt=\"\" /></p>', '2013-08-01 09:14:22', 'mav', '332', '/userfiles/image/File_huwko/DSC_0783.JPG', null, null, null, 'True', '101.51.134.170', 'A', '1', null);
INSERT INTO `hos_news` VALUES ('31', null, 'โครงการหมู่บ้านสร้างสุขภาพ ควบคุมโรคเข้มแข็งแบบยั่งยืน', '<p>&nbsp;ในช่วงเดือนกรกฎาคม 2556 เทศบาลตำบลทุ่งศรีชุมพล จัดสรรงบประมาณให้ทุกหมู่บ้าน จัดทำโครงการหมู่บ้านสร้างสุขภาพ ควบคุมโรคเข้มแข็งแบบยั่งยืน โดย อสม.เป็นผู้จัดทำโครงการและดำเนินการเอง จนท.รพ. ร่วมเป็นวิทยากร เข้มแข็งมาก มีการดำเนินการต่อเนื่องจากนี้ คือ ประกวดหมู่บ้านควบคุมโรคเข้มแข็ง และหมู่บ้านสร้างสุขภาพ&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Nutty/%E0%B8%AB%E0%B8%A1%E0%B8%B9%E0%B9%88%E0%B8%9A%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B8%AA%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%87%E0%B8%AA%E0%B8%B8%E0%B8%82%E0%B8%A0%E0%B8%B2%E0%B8%9E/IMG_1444.JPG\" width=\"800\" height=\"598\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Nutty/%E0%B8%AB%E0%B8%A1%E0%B8%B9%E0%B9%88%E0%B8%9A%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B8%AA%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%87%E0%B8%AA%E0%B8%B8%E0%B8%82%E0%B8%A0%E0%B8%B2%E0%B8%9E/IMG_1103.JPG\" width=\"800\" height=\"598\" alt=\"\" />&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Nutty/%E0%B8%AB%E0%B8%A1%E0%B8%B9%E0%B9%88%E0%B8%9A%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B8%AA%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%87%E0%B8%AA%E0%B8%B8%E0%B8%82%E0%B8%A0%E0%B8%B2%E0%B8%9E/IMG_1162.JPG\" width=\"800\" height=\"598\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Nutty/%E0%B8%AB%E0%B8%A1%E0%B8%B9%E0%B9%88%E0%B8%9A%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B8%AA%E0%B8%A3%E0%B9%89%E0%B8%B2%E0%B8%87%E0%B8%AA%E0%B8%B8%E0%B8%82%E0%B8%A0%E0%B8%B2%E0%B8%9E/IMG_1164.JPG\" width=\"800\" height=\"598\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Nutty/%E0%B8%AB%E0%B8%A1%E0%B8%B9%E0%B9%88%E0%B8%9A%E0%B9%89%E0%B8%B2%E0%B8%99%E0%B8%AA%E0%B