INSERT INTO `hos_news` VALUES ('30', null, 'เรียน เจ้าหน้าที่โรงพยาบาลชุมพลบุรีแจ้งเรื่องการลงทะเบียนเข้าใช้งานโปรแกรม P4P', '<p><span style=\"font-size: medium;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เนื่องด้วยคณะกรรมการบริหารโรงพยาบาล &nbsp;ได้แจ้งให้ทราบเกี่ยวกับการบันทึกการปฏิบัติงาน &nbsp;P4P</span></p>\r\n<p><span style=\"font-size: medium;\">ดังนั้นขอให้เจ้าหน้าที่ทุกท่านเข้าลงทะเบียนเพื่อใช้งานโปรแกรม ได้ที่</span></p>\r\n<p><span style=\"font-size: medium;\">&nbsp;คลิกเพื่อเข้าลงทะเบียน &nbsp;&nbsp;</span><a href=\"http://gis.phakdeehos.org/cphos/index.php\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: medium;\">http://gis.phakdeehos.org/cphos/index.php</span></span></a></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/File_huwko/p4p/p1.jpg\" width=\"1024\" height=\"706\" alt=\"\" /></p>\r\n<p><span style=\"font-size: medium;\">เลือกที่ </span><span style=\"color: rgb(128, 0, 0);\"><span style=\"font-size: medium;\">&nbsp;ลงทะเบียน &nbsp;</span><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: medium;\">จะปรากฏฟอร์มให้ระบุข้อมูลของท่านลงไป</span></span></span></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/File_huwko/p4p/p2.jpg\" width=\"1024\" height=\"662\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', '2013-07-31 08:58:14', 'admin', '289', '/userfiles/image/File_huwko/p4p/DSC_1514.jpg', null, null, null, 'True', '182.52.28.82', 'A', '1', null);
