INSERT INTO `hos_news` VALUES ('44', null, 'วันนี้ 28 สิงหาคม 2556  ขอเชิญเจ้าหน้าที่โรงพยาบาลเข้าร่วมฟังบรรยาการใช้งานระบบบริหารบุคลากรของโรงพยาบาลเวลา 13.30 น.', '<p><span style=\"color: rgb(153, 51, 0);\"><span lang=\"TH\" style=\"font-size: 20pt; line-height: 115%; font-family: \'Cordia New\', sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"color: rgb(128, 0, 0);\"><span lang=\"TH\" style=\"font-size: 20pt; line-height: 115%; font-family: \'Cordia New\', sans-serif;\">ด้วยงานคุณภาพโรงพยาบาลได้พัฒนาระบบงานเพื่อบริหารจัดการข้อมูลบุคลากร&nbsp; </span><span style=\"font-size: 20pt; line-height: 115%; font-family: \'Cordia New\', sans-serif;\">iHosOffice&nbsp; <span lang=\"TH\">จึงเรียนเชิญบุคลากรโรงพยาบาลที่เคารพทุกท่าน ในวันนี้เวลา 13.30 น ขอเชิญเข้ารับฟัง การแนะนำการใช้งานระบบบริหารบุคลากร&nbsp;&nbsp; การบันทึกไปประชุม&nbsp; การบันทึกการลา&nbsp; และการรายงานการบันทึกการเข้าทำงาน &nbsp;ณ ห้องประชุมโรงพยาบาล&nbsp; หากท่านใดมี </span>&nbsp;&nbsp;Smart Phone&nbsp; <span lang=\"TH\">&nbsp;</span>Tablet&nbsp;&nbsp;&nbsp; <span lang=\"TH\">รบกวนติดตัวไปด้วยเพื่อทดสอบการใช้งาน ระบบสารสนเทศนี้ด้วย &nbsp; </span></span></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(153, 51, 0);\"><span style=\"color: rgb(128, 0, 0);\"><span style=\"font-size: 20pt; line-height: 115%; font-family: \'Cordia New\', sans-serif;\"><span lang=\"TH\"><img src=\"/userfiles/image/File_huwko/13/1.jpg\" width=\"800\" height=\"652\" alt=\"\" />&nbsp;&nbsp;</span></span></span></span></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/File_huwko/13/2.jpg\" width=\"800\" height=\"539\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', '2013-08-28 03:15:53', 'huwko', '461', '/userfiles/image/File_huwko/13/1.jpg', null, null, null, 'True', '101.51.235.251', 'A', '1', null);
INSERT INTO `hos_news` VALUES ('46', null, 'ทำบุญประจำเดือน เสริมสร้างพลังแห่งความดี', '<p><span style=\"font-size: larger;\"><span style=\"font-family: \'Comic Sans MS\';\">&nbsp; &nbsp; ในทุกๆเดือน โรงพยาบาลชุมพลบุรี ได้จัดการทำบุญประจำเดือน ณ ห้องประชุมโรงพยาบาลชุมพลบุรี เวลา 07:00 น. เพื่อเสริมสร้างคุณธรรมและจริยธรรม&nbsp;<span style=\"color: rgb(34, 34, 34); line-height: 21px;\">การเริ่มต้นวันใหม่ด้วยการทำบุญ ทำให้จิตใจแจ่มใส เพื่อให้มีใจที่เข้มแข็ง ไม่ย่อมพ่ายแพ้ต่อบาปได้ง่าย&nbsp;</span></span></span></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0409(1).jpg\" width=\"800\" height=\"530\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0414.JPG\" width=\"812\" height=\"538\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0415.JPG\" width=\"800\" height=\"530\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0419.JPG\" width=\"800\" height=\"530\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0433.JPG\" width=\"812\" height=\"538\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>', '2013-08-28 04:52:25', 'mav', '400', '/userfiles/image/Image%20mikato/1A%2056/1k%20make%20merit/DSC_0442.JPG', null, null, null, 'True', '182.52.108.91', 'A', '1', null);
