INSERT INTO `hos_news` VALUES ('56', null, 'รายงานการประชุมกีฬาสาธารณสุขสัมพันธ์ ประจำปี 2557', '<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big><span lang=\"TH\" style=\"line-height: 115%;\">สำนักงานสาธารณสุขจังหวัดสุรินทร์&nbsp; จะจัดการแข่งขันกีฬาสาธารณสุขสัมพันธ์ ประจำปี </span><span style=\"line-height: 115%;\">2557 <span lang=\"TH\">โดยโซนสีเขียว(คปสอ.ท่าตูม) จะเป็นเจ้าภาพในการแข่งขันระดับโซน และโซนสีส้ม จะเป็นเจ้าภาพจัดการแข่งขันกีฬาสาธารณสุขสัมพันธ์ ประจำปี </span>2557<span lang=\"TH\"> ระดับจังหวัด&nbsp;</span></span></big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>โปรแกรมการแข่งขันกีฬาสาธารณสุขสัมพันธ์ ประจำปี 2557</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>- ระดับอำเภอ (โซนสี) &nbsp;&nbsp;</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>(เดือนพฤศจิกายน &ndash; ต้นเดือนธันวาคม)</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>- ระดับจังหวัด &nbsp;(เดือน มกราคม 2557)</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>เจ้าภาพกำหนด วันที่ 12,17-18 มกราคม 2557&nbsp;</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>สนามเทคโนโลยีราชมงคลอิสานสุรินทร์</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>หมายเหตุ...</big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big><span lang=\"TH\">งบประมาณที่ใช้ในการแข่งขัน</span></big></span></span><o:p></o:p></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big><span lang=\"TH\">- ระดับโซน&nbsp; </span>100,000 <span lang=\"TH\">บาท</span></big></span></span><o:p></o:p></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big><span lang=\"TH\">- ระดับจังหวัด&nbsp; </span>240,000 <span lang=\"TH\">บาท</span></big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big><span lang=\"TH\" style=\"text-align: justify; text-indent: 36pt;\">ปีนี้สำนักงานสาธารณสุขจังหวัดสุรินทร์ ไม่ได้กันงบประมาณที่ใช้ในการแข่งขันกีฬาสาธารณสุข(ไม่มีเงินให้) ทางงานส่งเสริมสุขภาพ ได้ชี้แจงว่า ไม่สามารถกันเงินไว้ใช้ที่จังหวัดได้และเงินได้โอนเข้าอำเภอหมดแล้ว ดังนั้นการแข่งขันกีฬาครั้งนี้จึงต้องอาศัยเงินจาก </span><span style=\"text-align: justify; text-indent: 36pt;\">CUP </span><span lang=\"TH\" style=\"text-align: justify; text-indent: 36pt;\">ลงขันในการจัดการแข่งขันทั้งระดับอำเภอและระดับจังหวัด ที่ประชุมมีมติรอการประชุมของผู้บริหารในระดับจังหวัดเพื่อรอดำเนินการต่อไป</span></big></span></span></h3>\r\n<h3><span style=\"font-size: medium;\"><span style=\"font-family: Arial;\"><big>(ปีนี้อาจจะงดการแข่งขันกีฬาครับ หากที่ประชุมผู้บริหารตกลงกันไม่ได้)</big></span></span></h3>\r\n<p class=\"MsoNoSpacing\" style=\"margin-left:108.0pt;text-align:justify;text-justify:\r\ninter-cluster;text-indent:36.0pt\"><o:p></o:p></p>\r\n<div>&nbsp;</div>', '2013-11-07 12:12:06', 'admin', '277', 'xxxnaturbo', null, null, null, 'True', '27.55.128.243', 'A', '1', null);
