INSERT INTO `hos_news` VALUES ('94', null, 'ระวัง 6 โรค มากับภัยหนาว', '<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%201.jpg\" width=\"800\" height=\"600\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%202.jpg\" width=\"800\" height=\"600\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%203.jpg\" width=\"800\" height=\"600\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%204.jpg\" width=\"800\" height=\"600\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%205.jpg\" width=\"800\" height=\"600\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\"><img src=\"/userfiles/image/Image%20mikato/57/cold/cold%206.jpg\" width=\"800\" height=\"600\" alt=\"\" />&nbsp;</p>\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 219); font-family: Tahoma; font-size: 14px; line-height: 20px;\">ทั้งนี้ กรมควบคุมโรค ได้เตือนเป็นพิเศษสำหรับกลุ่มประชาชนที่มีความเสี่ยง ได้แก่ ผู้สูงอายุ เด็กเล็ก ผู้ที่มีโรคประจำตัวเรื้อรัง เช่น โรคภูมิคุ้มกันบกพร่อง โรคเบาหวาน โรคหัวใจ โรคปอด โรคโลหิตจาง </span></p>\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 219); font-family: Tahoma; font-size: 14px; line-height: 20px;\">เนื่องจากมีระดับภูมิต้านทานโรคต่ำอยู่แล้ว โดยขอให้รักษาความอบอุ่นร่างกาย ดูแลสุขภาพให้แข็งแรง รับประทานอาหารที่มีประโยชน์ครบทั้ง 5 หมู่ ไม่ควรดื่มเครื่องดื่มมึนเมา ออกกำลังกายสม่ำเสมอ </span></p>\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 219); font-family: Tahoma; font-size: 14px; line-height: 20px;\">พักผ่อนให้เพียงพอ ล้างมือบ่อย ๆ เพื่อจะได้มีร่างกายแข็งแรง ไม่เจ็บป่วยด้วยโรคร้ายที่มักมาพร้อมกับหน้าหนาวนั่นเอง</span></p>', '2014-11-19 16:12:08', 'mav', '151', '/userfiles/image/Image%20mikato/57/cold/cold%20poster.jpg', null, null, null, 'True', '122.155.13.144', 'A', '1', null);
